import React from "react";

function Card({ title, value, description, backgroundColor = "#1565c0a4" }) {
    return (
        <div
            style={{
                border: "1px solid #ddd",
                borderRadius: "10px",
                padding: "20px",
                boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
                backgroundColor: backgroundColor,
                textAlign: "center",
                margin: "10px",
                width: "200px",
                flexShrink: 0,
            }}
        >
            <h3 style={{ margin: "0 0 10px", color: "#1565c0a4" }}>{title}</h3>
            <h1 style={{ margin: "0 0 10px", fontSize: "2.5rem", color: "#333" }}>
                {value}
            </h1>
            <p style={{ margin: "0", color: "#666" }}>{description}</p>
        </div>
    );
}

export default Card;
