import React, { useState } from "react";
import ReactDOM from "react-dom/client";

function App() {
    const [data, setData] = useState([]); // State to hold attendance data
    const [error, setError] = useState(""); // State to hold error messages
    const [loading, setLoading] = useState(false); // Loading state

    const fetchData = async () => {
        setLoading(true);
        setError("");

        try {
            const response = await fetch("http://192.168.29.179:5001/api/attendance/fetch");
            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }
            const result = await response.json();
            console.log("Fetched Data:", result);

            const updatedData = result.map((item) => {
                const hoursConducted = parseFloat(item.hoursConducted || 0);
                const hoursAbsent = parseFloat(item.hoursAbsent || 0);
                const attendancePercentage = parseFloat(item.attendancePercentage || 0);

                let marginCount = -1;
                let futureAbsent = hoursAbsent;
                let futureConducted = hoursConducted;

                // Calculate margin classes
                if (attendancePercentage >= 75) {
                    while (
                        (futureConducted - futureAbsent) / futureConducted * 100 > 75
                    ) {
                        marginCount++;
                        futureAbsent++;
                        futureConducted++;
                    }
                }

                // Calculate required classes
                const requiredClasses =
                    attendancePercentage < 75
                        ? (0.75 * hoursConducted - (hoursConducted - hoursAbsent)) / 0.25
                        : 0;

                return {
                    ...item,
                    requiredClasses: requiredClasses >= 0 ? requiredClasses : 0,
                    marginClasses: marginCount > 0 ? marginCount : 0,
                };
            });

            setData(updatedData);
        } catch (err) {
            console.error("Error fetching data:", err.message);
            setError("Failed to fetch data. Please try again.");
        } finally {
            setLoading(false);
        }
    };




    return (
        <div
            style={{
                fontFamily: "'Roboto', sans-serif",
                backgroundColor: "#f4f4f9",
                minHeight: "100vh",
                padding: "20px",
                color: "#333",
            }}
        >
            <h1
                style={{
                    textAlign: "center",
                    color: "#4CAF50",
                    marginBottom: "20px",
                    fontSize: "2.5rem",
                }}
            >
                Attendance Viewer
            </h1>
            <div style={{ textAlign: "center", marginBottom: "20px" }}>
                <button
                    onClick={fetchData}
                    style={{
                        padding: "12px 24px",
                        fontSize: "16px",
                        backgroundColor: "#4CAF50",
                        color: "#fff",
                        border: "none",
                        borderRadius: "5px",
                        cursor: "pointer",
                        boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
                        transition: "background-color 0.3s",
                    }}
                    disabled={loading}
                >
                    {loading ? "Loading..." : "Fetch Attendance"}
                </button>
                {error && (
                    <p style={{ color: "red", marginTop: "10px", fontSize: "1rem" }}>
                        {error}
                    </p>
                )}
            </div>
            <div style={{ display: "flex", flexDirection: "column", gap: "20px" }}>
                {data.map((course, index) => (
                    <div
                        key={index}
                        onClick={() => handleCardClick(course)}
                        style={{
                            padding: "20px",
                            backgroundColor: "#fff",
                            borderRadius: "10px",
                            boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
                            cursor: "pointer",
                            display: "flex",
                            flexDirection: "column",
                            gap: "10px",
                        }}
                    >
                        <h3 style={{ margin: 0, color: "#333" }}>{course.courseTitle}</h3>
                        <p style={{ margin: 0, color: "#666" }}>{course.facultyName}</p>
                        <p style={{ margin: 0, color: "#999" }}>
                            Attendance: {course.attendancePercentage}%
                        </p>
                    </div>
                ))}
            </div>

            {/* Modal for course details */}
            {selectedCourse && (
                <div
                    style={{
                        position: "fixed",
                        top: 0,
                        left: 0,
                        width: "100%",
                        height: "100%",
                        backgroundColor: "rgba(0, 0, 0, 0.5)",
                        display: "flex",
                        justifyContent: "center",
                        alignItems: "center",
                    }}
                    onClick={closeModal}
                >
                    <div
                        onClick={(e) => e.stopPropagation()} // Prevent closing when clicking inside the modal
                        style={{
                            backgroundColor: "#fff",
                            padding: "20px",
                            borderRadius: "10px",
                            width: "90%",
                            maxWidth: "500px",
                            boxShadow: "0 4px 8px rgba(0, 0, 0, 0.2)",
                        }}
                    >
                        <h2 style={{ color: "#4CAF50" }}>{selectedCourse.courseTitle}</h2>
                        <p>
                            <strong>Faculty Name:</strong> {selectedCourse.facultyName}
                        </p>
                        <p>
                            <strong>Attendance %:</strong>{" "}
                            {selectedCourse.attendancePercentage}
                        </p>
                        <p>
                            <strong>Hours Conducted:</strong>{" "}
                            {selectedCourse.hoursConducted}
                        </p>
                        <p>
                            <strong>Hours Absent:</strong> {selectedCourse.hoursAbsent}
                        </p>
                        <p>
                            <strong>Category:</strong> {selectedCourse.category}
                        </p>
                        <p>
                            <strong>Slot:</strong> {selectedCourse.slot}
                        </p>
                        <button
                            onClick={closeModal}
                            style={{
                                padding: "10px 20px",
                                backgroundColor: "#4CAF50",
                                color: "#fff",
                                border: "none",
                                borderRadius: "5px",
                                cursor: "pointer",
                                marginTop: "20px",
                            }}
                        >
                            Close
                        </button>
                    </div>
                </div>
            )}
        </div>
    );
}

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<App />);
