import React, { useState } from "react";
import ReactDOM from "react-dom/client";

function App() {
    const [data, setData] = useState([]); // State to hold attendance data
    const [error, setError] = useState(""); // State to hold error messages
    const [loading, setLoading] = useState(false); // Loading state

    const fetchData = async () => {
        setLoading(true);
        setError("");

        try {
            const response = await fetch("http://localhost:5001/api/attendance/fetch");
            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }
            const result = await response.json();
            console.log("Fetched Data:", result); // Log the fetched data
            const updatedData = result.map((item) => {
                // Calculate total classes
                const totalClasses = item.hoursConducted + item.hoursAbsent;
                const currentPercentage = item.attendancePercentage;

                // Calculate required classes to reach 75%
                const requiredClasses =
                    currentPercentage < 75
                        ? Math.ceil((0.75 * totalClasses - item.hoursConducted) / 0.25)
                        : 0;

                // Calculate margin classes to cut and stay above 75%
                const marginClasses =
                    currentPercentage > 75
                        ? Math.floor((item.hoursConducted - 0.75 * totalClasses) / 0.25)
                        : 0;

                return {
                    ...item,
                    requiredClasses,
                    marginClasses,
                };
            });
            setData(updatedData);
        } catch (err) {
            console.error("Error fetching data:", err.message);
            setError("Failed to fetch data. Please try again.");
        } finally {
            setLoading(false);
        }
    };


    return (
        <div
            style={{
                padding: "20px",
                fontFamily: "'Roboto', sans-serif",
                backgroundColor: "#f4f4f9",
                minHeight: "100vh",
                color: "#333",
            }}
        >
            <h1
                style={{
                    textAlign: "center",
                    color: "#4CAF50",
                    marginBottom: "20px",
                    fontSize: "2.5rem",
                }}
            >
                Attendance Viewer
            </h1>
            <div style={{ textAlign: "center" }}>
                <button
                    onClick={fetchData}
                    style={{
                        padding: "12px 24px",
                        fontSize: "16px",
                        backgroundColor: "#4CAF50",
                        color: "#fff",
                        border: "none",
                        borderRadius: "5px",
                        cursor: "pointer",
                        boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
                        transition: "background-color 0.3s",
                    }}
                    onMouseOver={(e) => (e.target.style.backgroundColor = "#45a049")}
                    onMouseOut={(e) => (e.target.style.backgroundColor = "#4CAF50")}
                    disabled={loading}
                >
                    {loading ? "Loading..." : "Fetch Data"}
                </button>
                {error && (
                    <p style={{ color: "red", marginTop: "10px", fontSize: "1rem" }}>
                        {error}
                    </p>
                )}
            </div>
            <div style={{ marginTop: "20px" }}>
                <table
                    style={{
                        width: "100%",
                        borderCollapse: "collapse",
                        margin: "20px auto",
                        backgroundColor: "#fff",
                        boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
                    }}
                >
                    <thead>
                        <tr
                            style={{
                                backgroundColor: "#4CAF50",
                                color: "#fff",
                                fontWeight: "bold",
                                fontSize: "1rem",
                            }}
                        >
                            <th style={{ padding: "10px", border: "1px solid #ddd" }}>
                                Course Code
                            </th>
                            <th style={{ padding: "10px", border: "1px solid #ddd" }}>
                                Course Title
                            </th>
                            <th style={{ padding: "10px", border: "1px solid #ddd" }}>
                                Attendance %
                            </th>
                            <th style={{ padding: "10px", border: "1px solid #ddd" }}>
                                Hours Conducted
                            </th>
                            <th style={{ padding: "10px", border: "1px solid #ddd" }}>
                                Hours Absent
                            </th>
                            <th style={{ padding: "10px", border: "1px solid #ddd" }}>
                                Required Classes
                            </th>
                            <th style={{ padding: "10px", border: "1px solid #ddd" }}>
                                Margin Classes
                            </th>
                        </tr>
                    </thead>
                    <tbody>
                        {data.length > 0 ? (
                            data.map((item, index) => (
                                <tr
                                    key={index}
                                    style={{
                                        backgroundColor: index % 2 === 0 ? "#f9f9f9" : "#fff",
                                        textAlign: "center",
                                    }}
                                >
                                    <td style={{ padding: "10px", border: "1px solid #ddd" }}>
                                        {item.courseCode}
                                    </td>
                                    <td style={{ padding: "10px", border: "1px solid #ddd" }}>
                                        {item.courseTitle}
                                    </td>
                                    <td style={{ padding: "10px", border: "1px solid #ddd" }}>
                                        {item.attendancePercentage}%
                                    </td>
                                    <td style={{ padding: "10px", border: "1px solid #ddd" }}>
                                        {item.hoursConducted}
                                    </td>
                                    <td style={{ padding: "10px", border: "1px solid #ddd" }}>
                                        {item.hoursAbsent}
                                    </td>
                                    <td style={{ padding: "10px", border: "1px solid #ddd" }}>
                                        {item.requiredClasses > 0 ? item.requiredClasses : "-"}
                                    </td>
                                    <td style={{ padding: "10px", border: "1px solid #ddd" }}>
                                        {item.marginClasses > 0 ? item.marginClasses : "-"}
                                    </td>
                                </tr>
                            ))
                        ) : (
                            <tr>
                                <td
                                    colSpan="7"
                                    style={{
                                        textAlign: "center",
                                        padding: "20px",
                                        color: "#666",
                                    }}
                                >
                                    No data available
                                </td>
                            </tr>
                        )}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<App />);
