import React from "react";

function Card({ courseTitle, attendancePercentage, marginClasses, requiredClasses }) {
    return (
        <div
            style={{
                border: "1px solid #1e3a8a", // Blue border
                borderRadius: "10px",
                padding: "20px",
                backgroundColor: "#1e293b", // Dark theme card background
                color: "#e3f2fd", // Light text color
                margin: "10px 0",
                boxShadow: "0 4px 8px rgba(0, 0, 0, 0.3)",
            }}
        >
            <h3 style={{ marginBottom: "10px", color: "#64b5f6" }}>{courseTitle}</h3>
            <p>
                <strong>Attendance:</strong> {attendancePercentage}%
            </p>
            <p>
                <strong>Margin Classes:</strong> {marginClasses}
            </p>
            <p>
                <strong>Required Classes:</strong> {requiredClasses}
            </p>
        </div>
    );
}

export default Card;
