const React = require("react");
const ReactDOM = require("react-dom/client");
const axios = require("axios");

function App() {
    const [data, setData] = React.useState([]); // State to hold attendance data
    const [error, setError] = React.useState(""); // State to hold error messages
    const [loading, setLoading] = React.useState(false); // Loading state

    function fetchData() {
        setLoading(true); // Set loading to true
        setError(""); // Clear any existing errors

        // Fetch data using Axios
        axios
            .get("http://localhost:5001/api/attendance/fetch")
            .then((response) => {
                setData(response.data); // Update state with fetched data
            })
            .catch((err) => {
                console.error("Error fetching data:", err.message); // Debugging
                setError("Failed to fetch data. Please try again."); // Set error message
            })
            .finally(() => {
                setLoading(false); // Set loading to false after completion
            });
    }

    // Render the application UI
    return React.createElement(
        "div",
        { style: { padding: "20px", fontFamily: "Arial" } },
        React.createElement("h1", null, "Attendance Viewer"),
        React.createElement(
            "button",
            {
                onClick: fetchData,
                style: {
                    padding: "10px 20px",
                    fontSize: "16px",
                    marginBottom: "20px",
                    cursor: "pointer",
                },
                disabled: loading, // Disable the button while loading
            },
            loading ? "Loading..." : "Fetch Data"
        ),
        error && React.createElement("p", { style: { color: "red" } }, error),
        React.createElement(
            "table",
            { border: "1", style: { width: "100%", textAlign: "center" } },
            React.createElement(
                "thead",
                null,
                React.createElement(
                    "tr",
                    null,
                    React.createElement("th", null, "Course Code"),
                    React.createElement("th", null, "Course Title"),
                    React.createElement("th", null, "Category"),
                    React.createElement("th", null, "Faculty Name"),
                    React.createElement("th", null, "Slot"),
                    React.createElement("th", null, "Hours Conducted"),
                    React.createElement("th", null, "Hours Absent"),
                    React.createElement("th", null, "Attendance %")
                )
            ),
            React.createElement(
                "tbody",
                null,
                data.map(function (item, index) {
                    return React.createElement(
                        "tr",
                        { key: index },
                        React.createElement("td", null, item.courseCode),
                        React.createElement("td", null, item.courseTitle),
                        React.createElement("td", null, item.category),
                        React.createElement("td", null, item.facultyName),
                        React.createElement("td", null, item.slot),
                        React.createElement("td", null, item.hoursConducted),
                        React.createElement("td", null, item.hoursAbsent),
                        React.createElement("td", null, item.attendancePercentage)
                    );
                })
            )
        )
    );
}

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(React.createElement(App));
