import React from "react";

const AttendanceTable = ({ data }) => {
    return (
        <table style={styles.table}>
            <thead>
                <tr>
                    {["Course Code", "Course Title", "Category", "Faculty Name", "Slot", "Hours Conducted", "Hours Absent", "Attendance %"].map((header, index) => (
                        <th key={index} style={styles.headerCell}>{header}</th>
                    ))}
                </tr>
            </thead>
            <tbody>
                {data.length > 0 ? (
                    data.map((item, index) => (
                        <tr key={index} style={index % 2 === 0 ? styles.rowEven : styles.rowOdd}>
                            <td style={styles.cell}>{item.courseCode}</td>
                            <td style={styles.cell}>{item.courseTitle}</td>
                            <td style={styles.cell}>{item.category}</td>
                            <td style={styles.cell}>{item.facultyName}</td>
                            <td style={styles.cell}>{item.slot}</td>
                            <td style={styles.cell}>{item.hoursConducted}</td>
                            <td style={styles.cell}>{item.hoursAbsent}</td>
                            <td style={styles.cell}>{item.attendancePercentage}</td>
                        </tr>
                    ))
                ) : (
                    <tr>
                        <td colSpan="8" style={styles.noDataCell}>No data available</td>
                    </tr>
                )}
            </tbody>
        </table>
    );
};

const styles = {
    table: {
        width: "100%",
        borderCollapse: "collapse",
        marginTop: "20px",
    },
    headerCell: {
        padding: "12px",
        backgroundColor: "#4CAF50",
        color: "white",
        textAlign: "center",
        fontSize: "16px",
        fontWeight: "bold",
    },
    rowEven: {
        backgroundColor: "#f2f2f2",
    },
    rowOdd: {
        backgroundColor: "white",
    },
    cell: {
        padding: "10px",
        textAlign: "center",
        fontSize: "14px",
        border: "1px solid #ddd",
    },
    noDataCell: {
        textAlign: "center",
        padding: "15px",
        color: "gray",
        fontStyle: "italic",
    },
};

export default AttendanceTable;
