const React = require("react");
const ReactDOM = require("react-dom/client");
const axios = require("axios");

function App() {
    const [data, setData] = React.useState([]);
    const [error, setError] = React.useState("");
    const [loading, setLoading] = React.useState(false);

    function fetchData() {
        setLoading(true);
        setError("");
        axios
            .get("http://localhost:5001/api/attendance/fetch") // Change this to your backend API endpoint
            .then((response) => {
                setData(response.data);
            })
            .catch((err) => {
                setError("Failed to fetch data");
            })
            .finally(() => {
                setLoading(false);
            });
    }

    return React.createElement(
        "div",
        { style: { padding: "20px", fontFamily: "Arial" } },
        React.createElement("h1", null, "Attendance Data"),
        React.createElement(
            "button",
            {
                onClick: fetchData,
                style: {
                    padding: "10px 20px",
                    fontSize: "16px",
                    margin: "10px 0",
                    cursor: "pointer",
                },
            },
            loading ? "Loading..." : "Fetch Data"
        ),
        error && React.createElement("p", { style: { color: "red" } }, error),
        React.createElement(
            "table",
            { border: "1", style: { width: "100%", marginTop: "20px", textAlign: "center" } },
            React.createElement(
                "thead",
                null,
                React.createElement(
                    "tr",
                    null,
                    React.createElement("th", null, "Course Code"),
                    React.createElement("th", null, "Course Title"),
                    React.createElement("th", null, "Category"),
                    React.createElement("th", null, "Faculty Name"),
                    React.createElement("th", null, "Slot"),
                    React.createElement("th", null, "Hours Conducted"),
                    React.createElement("th", null, "Hours Absent"),
                    React.createElement("th", null, "Attendance %")
                )
            ),
            React.createElement(
                "tbody",
                null,
                data.map(function (item, index) {
                    return React.createElement(
                        "tr",
                        { key: index },
                        React.createElement("td", null, item.courseCode),
                        React.createElement("td", null, item.courseTitle),
                        React.createElement("td", null, item.category),
                        React.createElement("td", null, item.facultyName),
                        React.createElement("td", null, item.slot),
                        React.createElement("td", null, item.hoursConducted),
                        React.createElement("td", null, item.hoursAbsent),
                        React.createElement("td", null, item.attendancePercentage)
                    );
                })
            )
        )
    );
}

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(React.createElement(App));
