import React, { useState } from "react";
import ReactDOM from "react-dom/client";

function App() {
    const [data, setData] = useState([]); // State to hold attendance data
    const [error, setError] = useState(""); // State to hold error messages
    const [loading, setLoading] = useState(false); // Loading state

    const fetchData = async () => {
        setLoading(true);
        setError("");

        try {
            const response = await fetch("http://localhost:5001/api/attendance/fetch");
            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }
            const result = await response.json();
            console.log('Fetched Data:', result); // Log the fetched data
            setData(result); // Update the state with fetched data
        } catch (err) {
            console.error("Error fetching data:", err.message);
            setError("Failed to fetch data. Please try again.");
        } finally {
            setLoading(false);
        }
    };


    return (
        <div style={{ padding: "20px", fontFamily: "Arial" }}>
            <h1>Attendance Viewer</h1>
            <button
                onClick={fetchData}
                style={{
                    padding: "10px 20px",
                    fontSize: "16px",
                    marginBottom: "20px",
                    cursor: "pointer",
                }}
                disabled={loading} // Disable button while loading
            >
                {loading ? "Loading..." : "Fetch Data"}
            </button>
            {error && <p style={{ color: "red" }}>{error}</p>}
            <table border="1" style={{ width: "100%", textAlign: "center" }}>
                <thead>
                    <tr>
                        <th>Course Code</th>
                        <th>Course Title</th>
                        <th>Category</th>
                        <th>Faculty Name</th>
                        <th>Slot</th>
                        <th>Hours Conducted</th>
                        <th>Hours Absent</th>
                        <th>Attendance %</th>
                    </tr>
                </thead>
                <tbody>
                    {data.length > 0 ? (
                        data.map((item, index) => (
                            <tr key={index}>
                                <td>{item.courseCode}</td>
                                <td>{item.courseTitle}</td>
                                <td>{item.category}</td>
                                <td>{item.facultyName}</td>
                                <td>{item.slot}</td>
                                <td>{item.hoursConducted}</td>
                                <td>{item.hoursAbsent}</td>
                                <td>{item.attendancePercentage}</td>
                            </tr>
                        ))
                    ) : (
                        <tr>
                            <td colSpan="8" style={{ textAlign: "center" }}>
                                No data available
                            </td>
                        </tr>
                    )}
                </tbody>
            </table>
        </div>
    );
}

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<App />);
