import React from "react";
import PropTypes from "prop-types";

function Card({
    courseTitle,
    slot,
    category,
    facultyName,
    attendancePercentage,
    hoursConducted,
    hoursAbsent,
    marginClasses,
    requiredClasses,
}) {
    const getPercentageColor = (percentage) => (percentage >= 75 ? "#4caf50" : "#e57373");

    return (
        <div
            style={{
                border: "1px solid #1e3a8a",
                borderRadius: "10px",
                padding: "20px",
                backgroundColor: "#1e293b",
                color: "#e3f2fd",
                margin: "10px 0",
                boxShadow: "0 4px 8px rgba(0, 0, 0, 0.3)",
            }}
        >
            <h3 style={{ marginBottom: "10px", color: "#64b5f6" }}>{courseTitle}</h3>
            <p style={{ color: "#ffd54f", fontWeight: "bold" }}>
                <strong>Slot:</strong> {slot}
            </p>
            <p>
                <strong>Category:</strong> {category}
            </p>
            <p>
                <strong>Faculty:</strong> {facultyName}
            </p>
            <p>
                <strong>Total Hours:</strong> {hoursConducted}
            </p>
            <p>
                <strong>Hours Absent:</strong> {hoursAbsent}
            </p>
            <p style={{ color: getPercentageColor(attendancePercentage) }}>
                <strong>Attendance:</strong> {attendancePercentage}%{" "}
                {attendancePercentage >= 75 ? "üëç" : "üëé"}
            </p>
            <p style={{ color: "#ff7043", fontWeight: "bold" }}>
                <strong>Required Classes:</strong> {requiredClasses}
            </p>
            <p style={{ color: "#64b5f6", fontWeight: "bold" }}>
                <strong>Margin Classes:</strong> {marginClasses}
            </p>
        </div>
    );
}

// PropTypes Validation
Card.propTypes = {
    courseTitle: PropTypes.string.isRequired,
    slot: PropTypes.string.isRequired,
    category: PropTypes.string.isRequired,
    facultyName: PropTypes.string.isRequired,
    attendancePercentage: PropTypes.number.isRequired,
    hoursConducted: PropTypes.number.isRequired,
    hoursAbsent: PropTypes.number.isRequired,
    marginClasses: PropTypes.number.isRequired,
    requiredClasses: PropTypes.number.isRequired,
};

// Default Props (optional)
Card.defaultProps = {
    courseTitle: "N/A",
    slot: "N/A",
    category: "N/A",
    facultyName: "N/A",
    attendancePercentage: 0,
    hoursConducted: 0,
    hoursAbsent: 0,
    marginClasses: 0,
    requiredClasses: 0,
};

export default Card;
