import React from "react";

function Card({
    courseTitle,
    slot,
    category,
    facultyName,
    attendancePercentage,
    hoursConducted,
    hoursAbsent,
    marginClasses,
    requiredClasses,
}) {
    // Helper functions for dynamic styles
    const getPercentageColor = (percentage) => (percentage >= 75 ? "#4caf50" : "#e57373");
    const highlightColor = "#ffd54f"; // Slot highlight color
    const marginColor = "#64b5f6"; // Margin classes highlight color
    const requiredColor = "#ff7043"; // Required classes highlight color

    return (
        <div
            style={{
                border: "1px solid #1e3a8a", // Blue border
                borderRadius: "10px",
                padding: "20px",
                backgroundColor: "#1e293b", // Dark theme card background
                color: "#e3f2fd", // Light text color
                margin: "10px 0",
                boxShadow: "0 4px 8px rgba(0, 0, 0, 0.3)",
            }}
        >
            {/* Course Title */}
            <h3 style={{ marginBottom: "10px", color: "#64b5f6" }}>
                {courseTitle}
            </h3>

            {/* Slot */}
            <p style={{ margin: "10px 0", color: highlightColor, fontWeight: "bold" }}>
                <strong>Slot:</strong> {slot}
            </p>

            {/* Category and Faculty Name */}
            <p style={{ margin: "5px 0" }}>
                <strong>Category:</strong> {category}
            </p>
            <p style={{ margin: "5px 0" }}>
                <strong>Faculty:</strong> {facultyName}
            </p>

            {/* Attendance Summary */}
            <p style={{ margin: "10px 0" }}>
                <strong>Total Hours:</strong> {hoursConducted}
            </p>
            <p style={{ margin: "5px 0" }}>
                <strong>Hours Absent:</strong> {hoursAbsent}
            </p>
            <p style={{ margin: "5px 0" }}>
                <strong style={{ color: getPercentageColor(attendancePercentage) }}>
                    Overall Attendance:
                </strong>{" "}
                {attendancePercentage}%{" "}
                {attendancePercentage >= 75 ? "üëç" : "üëé"}
            </p>

            {/* Highlighted Metrics */}
            <p style={{ margin: "10px 0", color: requiredColor, fontWeight: "bold" }}>
                <strong>Required Classes:</strong> {requiredClasses}
            </p>
            <p style={{ margin: "10px 0", color: marginColor, fontWeight: "bold" }}>
                <strong>Margin Classes:</strong> {marginClasses}
            </p>
        </div>
    );
}

export default Card;
